import styled from "styled-components";

export const HeaderWraper = styled.header `
width: 100%;
    display: flex;
    justify-content: space-between;
    border-bottom: 2px white solid ;
    align-items: center;

    .logo{
        width: 120px;
        height: 120px;
        img{
            max-width: 100%;
            max-height: 100%;
        }
    }
    nav{
        flex: 1 1 auto;
        display: flex;
        justify-content: space-evenly;
    }

`
